namespace Projects\BlogBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * CategoryRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class CategoryRepository extends EntityRepository
{
    /**
     * Get all
     * 
     * @return array
     */
    public function getAll()
    {
        /*$query = "SELECT c.id, c.slug, c.title, c.description, COUNT(c.id) AS counter
                  FROM ProjectsBlogBundle:Category c, ProjectsBlogBundle:Post p
                  WHERE c.id = p.categoryId";*/

        $query = "SELECT c
                  FROM ProjectsBlogBundle:Category c";

        return $this->getEntityManager()->createQuery($query)
                                        ->getResult();
    }

    /**
     * Get by slug
     * 
     * @param  string $slug Slug
     * @return array
     */
    public function getBySlug($slug)
    {
        $query = "SELECT c
                  FROM ProjectsBlogBundle:Category c
                  WHERE c.slug = ?1";

        try {
            return $this->getEntityManager()->createQuery($query)
                                            ->setParameter(1, $slug)
                                            ->getSingleResult();
        } catch(NoResultException $e) {
            return null;
        }
    }
}
